  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        title: Text("Audio Player"),
        centerTitle: true,
        backgroundColor: Colors.cyan,
        elevation: 0,
