class Player extends StatefulWidget {
  const Player({Key? key}) : super(key: key);

  @override
  State<Player> createState() => _PlayerState();
}

class _PlayerState extends State<Player> {
  bool isPlaying = true;
  final assetsAudioPlayer = AssetsAudioPlayer();
  double currentPositionInSeconds = 0;
  double songDurationInSeconds = 0;
  String currentPosition = "0:0:0";
  String songDuration = "0:0:0";

  @override
  void initState() {
    super.initState();
    assetsAudioPlayer.open(
      Playlist(
        audios: songs
            .map((e) => Audio(
          e.song,
          metas: Metas(
            title: e.title,
            artist: e.artist,
            album: e.album,
            image:
            MetasImage.network(e.img), //can be MetasImage.network
          ),
        ))
            .toList(),
        startIndex: Index,
      ),
      showNotification: true,
      autoStart: true,
    );
    assetsAudioPlayer.current.listen((Playing? playing) {
      songDuration = playing!.audio.duration.toString().split(".")[0];
      songDurationInSeconds = playing.audio.duration.inSeconds.toDouble();
      setState(() {});
    });

    assetsAudioPlayer.currentPosition.listen((Duration? duration) {
      currentPosition = duration.toString().split(".")[0];
      currentPositionInSeconds = duration!.inSeconds.toDouble();
      setState(() {});
    });
  }

  @override
  Widget build(BuildContext context) {
    return WillPopScope(
      onWillPop: () async {
        await assetsAudioPlayer.stop();
        return true;
      },
      child: Scaffold(
        body: Container(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.end,
            children: [
              Container(
                margin: const EdgeInsets.only(bottom: 50),
                height: MediaQuery.of(context).size.width * 0.5,
                width: MediaQuery.of(context).size.width * 0.5,
                decoration: BoxDecoration(
                    color: Colors.white,
                    image: DecorationImage(
                      fit: BoxFit.cover,
                      image: NetworkImage(songs[Index].img),
                    ),
                    borderRadius: BorderRadius.circular(20),
                    boxShadow: [
                      BoxShadow(
                          color: Colors.black.withOpacity(0.10),
                          blurRadius: 20,
                          offset: const Offset(0, -4),
                          spreadRadius: 0)
                    ]),
              ),
              Text(
                songs[Index].title,
                style: const TextStyle(
                    fontSize: 35,
                    fontWeight: FontWeight.w500,
                    color: Colors.black87),
              ),
              const SizedBox(
                height: 10,
              ),
              Text(
                songs[Index].artist,
                style: const TextStyle(
                    fontSize: 15,
                    fontWeight: FontWeight.w500,
                    color: Colors.black87),
              ),
              const SizedBox(
                height: 50,
              ),
              Container(
                padding: const EdgeInsets.all(10),
                height: MediaQuery.of(context).size.height * 0.35,
                width: MediaQuery.of(context).size.width,
                decoration: BoxDecoration(
                    color: Colors.white,
                    borderRadius: const BorderRadius.only(
                        topLeft: Radius.circular(60),
                        topRight: Radius.circular(60)),
                    boxShadow: [
                      BoxShadow(
                          color: Colors.black.withOpacity(0.10),
                          blurRadius: 20,
                          offset: const Offset(0, -4),
                          spreadRadius: 0)
                    ]),
                alignment: Alignment.center,
                child: Column(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    Padding(
                      padding: const EdgeInsets.symmetric(horizontal: 25),
                      child: Row(
                        mainAxisAlignment: MainAxisAlignment.spaceBetween,
                        children: [
                          Text(
                            currentPosition,
                            style: const TextStyle(
                                fontSize: 16,
                                fontWeight: FontWeight.bold,
                                color: Colors.black),
                          ),
                          Text(
                            songDuration,
                            style: const TextStyle(
                                fontSize: 16,
                                fontWeight: FontWeight.w600,
                                color: Colors.black),
                          )
                        ],
                      ),
                    ),
                    SliderTheme(
                      data: SliderTheme.of(context).copyWith(
                        trackHeight: 5,
                        thumbShape: SliderComponentShape.noThumb,
                      ),
                      child: Slider(
                          thumbColor: Colors.transparent,
                          activeColor: const Color(0xff590D2B),
                          inactiveColor: const Color(0xffEFF1F7),
                          value: currentPositionInSeconds,
                          min: 0,
                          max: songDurationInSeconds,
                          onChanged: (val) {
                            setState(() {
                              assetsAudioPlayer
                                  .seek(Duration(seconds: val.toInt()));
                            });
                          }),
                    ),
                    Row(
                      mainAxisAlignment: MainAxisAlignment.spaceBetween,
                      crossAxisAlignment: CrossAxisAlignment.center,
                      children: [
                        const SizedBox(
                          width: 1,
                        ),
                        Transform.scale(
                          scale: 0.8,
                          child: FloatingActionButton(
                            backgroundColor: Colors.transparent,
                            elevation: 0,
                            onPressed: () async {
                              assetsAudioPlayer.shuffle;
                            },
                            child: const Icon(
                              Icons.shuffle,
                              color: Color(0xff590D2B),
                            ),
                          ),
                        ),
                        FloatingActionButton(
                          elevation: 0,
                          backgroundColor: const Color(0xFFE8F0FA),
                          onPressed: () async {
                            await assetsAudioPlayer.previous();
                            if (Index > 0) {
                              Index--;
                            }
                            setState(() {});
                          },
                          child: const Icon(
                            Icons.skip_previous,
                            color: Color(0xff590D2B),
                          ),
                        ),
                        const SizedBox(
                          width: 1,
                        ),
                        Transform.scale(
                          scale: 1.3,
                          child: FloatingActionButton(
                            elevation: 0,
                            backgroundColor: const Color(0xff590D2B),
                            onPressed: () async {
                              await assetsAudioPlayer.playOrPause();
                              isPlaying = !isPlaying;
                              setState(() {});
                            },
                            child: Icon(
                                (isPlaying) ? Icons.pause : Icons.play_arrow),
                          ),
                        ),
                        const SizedBox(
                          width: 1,
                        ),
                        FloatingActionButton(
                          elevation: 0,
                          backgroundColor: const Color(0xFFE8F0FA),
                          onPressed: () async {
                            await assetsAudioPlayer.next();
                            if (Index < songs.length) {
                              Index++;
                            }
                            setState(() {});
                          },
                          child: const Icon(
                            Icons.skip_next,
                            color: Color(0xff590D2B),
                          ),
                        ),
                        Transform.scale(
                          scale: 0.8,
                          child: FloatingActionButton(
                            backgroundColor: Colors.transparent,
                            elevation: 0,
                            onPressed: () async {},
                            child: const Icon(
                              Icons.loop_rounded,
                              color: Color(0xff590D2B),
                            ),
                          ),
                        ),
                        const SizedBox(
                          width: 1,
                        ),
                      ],
                    ),
                  ],
                ),
              ),
            ],
          ),
          height: MediaQuery.of(context).size.height,
          width: MediaQuery.of(context).size.width,
          decoration: const BoxDecoration(
            gradient: LinearGradient(
              colors: [
                Color(0xffF9F9FB),
                Color(0xFFEFF1F3),
              ],
            ),
          ),
        ),
      ),
    );
  }
}



